#include<stdio.h>

int fakt(int x)
{
    int i,sonuc=1;
    for(i=1;i<=x;i++)
    sonuc=sonuc*i;
    return sonuc;
     
    
}

int perm(int n,int r)
{
    
    
 return fakt(n)/fakt(n-r);   
    
    
    
}
int komb(int n,int r)
{
    
return fakt(n)/fakt(n-r)*fakt(r);    
    
    
}



